import { useState } from 'react';

function App() {
  const [journeys, setJourneys] = useState([]);

useEffect(() => {
  async function load() {
    const res = await fetch("/api/journeys"); 
    const data = await res.json();

    console.log("API response:", data); // xem API trả gì

    if (Array.isArray(data)) {
      setJourneys(data);
    } else if (Array.isArray(data.journeys)) {
      setJourneys(data.journeys);
    } else if (Array.isArray(data.content)) {
      setJourneys(data.content);
    } else {
      setJourneys([]);
    }
  }
  load();
}, []);

  const loadJourneys = () => {
    fetch("/api/journeys?ownerId=owner1")   // dùng relative path
      .then(res => res.json())
      .then(data => setJourneys(data))
      .catch(err => console.error(err));
  };

  return (
    <div>
      <h1>Danh sách hành trình</h1>
      <button onClick={loadJourneys}>Xem hành trình</button>
      <ul>
        {journeys.map(j => (
          <li key={j.id}>
            ID: {j.id}, Quãng đường: {j.distanceKm} km, CO2 giảm: {j.savedKgCO2} kg
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
